contracts/ChatRoom.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// Note: FHEVM uses bytes for encrypted data storage
// Encryption/decryption happens on the client side using fhevmjs

/**
 * @title ChatRoom
 * @dev Main contract for managing chat rooms, users, and encrypted messages
 */
contract ChatRoom {
    // User profile structure
    struct UserProfile {
        string nickname;
        bool exists;
        uint256 createdAt;
    }

    // Room structure
    struct Room {
        string name;
        string description;
        address creator;
        uint256 createdAt;
        bool exists;
    }

    // Message structure with encrypted content
    struct Message {
        address sender;
        uint256 roomId;
        bytes encryptedContent; // Encrypted message content (bytes for FHEVM)
        uint256 timestamp;
        uint256 messageId;
        bool edited;
        uint256 editTimestamp;
    }

    // Mapping: user address => UserProfile
    mapping(address => UserProfile) public userProfiles;

    // Mapping: roomId => Room
    mapping(uint256 => Room) public rooms;

    // Mapping: roomId => messageId => Message
    mapping(uint256 => mapping(uint256 => Message)) public messages;

    // Mapping: roomId => messageCount
    mapping(uint256 => uint256) public roomMessageCounts;

    // Total rooms counter
    uint256 public totalRooms;

    // Total messages counter (global)
    uint256 public totalMessages;

    // Events
    event UserRegistered(address indexed user, string nickname);
    event NicknameUpdated(address indexed user, string newNickname);
    event RoomCreated(uint256 indexed roomId, string name, address creator);
    event MessageSent(
        uint256 indexed roomId,
        uint256 indexed messageId,
        address indexed sender,
        uint256 timestamp
    );
    event MessageEdited(
        uint256 indexed roomId,
        uint256 indexed messageId,
        address indexed sender,
        uint256 timestamp
    );

    /**
     * @dev Register a new user with nickname
     * @param nickname The nickname for the user
     */
    function registerUser(string memory nickname) external {
        require(!userProfiles[msg.sender].exists, "User already registered");
        require(bytes(nickname).length > 0, "Nickname cannot be empty");
        require(bytes(nickname).length <= 32, "Nickname too long");

        userProfiles[msg.sender] = UserProfile({
            nickname: nickname,
            exists: true,
            createdAt: block.timestamp
        });

        emit UserRegistered(msg.sender, nickname);
    }

    /**
     * @dev Update user nickname
     * @param newNickname The new nickname
     */
    function updateNickname(string memory newNickname) external {
        require(userProfiles[msg.sender].exists, "User not registered");
        require(bytes(newNickname).length > 0, "Nickname cannot be empty");
        require(bytes(newNickname).length <= 32, "Nickname too long");

        userProfiles[msg.sender].nickname = newNickname;
        emit NicknameUpdated(msg.sender, newNickname);
    }

    /**
     * @dev Get user nickname
     * @param user Address of the user
     * @return nickname The user's nickname
     */
    function getUserNickname(address user) external view returns (string memory) {
        require(userProfiles[user].exists, "User not registered");
        return userProfiles[user].nickname;
    }

    /**
     * @dev Check if user is registered
     * @param user Address of the user
     * @return exists True if user is registered
     */
    function isUserRegistered(address user) external view returns (bool) {
        return userProfiles[user].exists;
    }

    /**
     * @dev Create a new chat room
     * @param name Room name
     * @param description Room description
     * @return roomId The ID of the created room
     */
    function createRoom(string memory name, string memory description) external returns (uint256) {
        require(userProfiles[msg.sender].exists, "User must be registered");
        require(bytes(name).length > 0, "Room name cannot be empty");
        require(bytes(name).length <= 64, "Room name too long");

        uint256 roomId = totalRooms;
        rooms[roomId] = Room({
            name: name,
            description: description,
            creator: msg.sender,
            createdAt: block.timestamp,
            exists: true
        });

        totalRooms++;
        emit RoomCreated(roomId, name, msg.sender);
        return roomId;
    }

    /**
     * @dev Get room information
     * @param roomId The room ID
     * @return name Room name
     * @return description Room description
     * @return creator Room creator address
     * @return createdAt Creation timestamp
     */
    function getRoom(uint256 roomId) external view returns (
        string memory name,
        string memory description,
        address creator,
        uint256 createdAt
    ) {
        require(rooms[roomId].exists, "Room does not exist");
        Room memory room = rooms[roomId];
        return (room.name, room.description, room.creator, room.createdAt);
    }

    /**
     * @dev Send an encrypted message to a room
     * @param roomId The room ID
     * @param encryptedContent The encrypted message content (bytes)
     * @return messageId The ID of the sent message
     */
    function sendMessage(uint256 roomId, bytes calldata encryptedContent) external returns (uint256) {
        require(userProfiles[msg.sender].exists, "User must be registered");
        require(rooms[roomId].exists, "Room does not exist");

        uint256 messageId = roomMessageCounts[roomId];
        messages[roomId][messageId] = Message({
            sender: msg.sender,
            roomId: roomId,
            encryptedContent: encryptedContent,
            timestamp: block.timestamp,
            messageId: messageId,
            edited: false,
            editTimestamp: 0
        });

        roomMessageCounts[roomId]++;
        totalMessages++;

        emit MessageSent(roomId, messageId, msg.sender, block.timestamp);
        return messageId;
    }

    /**
     * @dev Edit an existing message
     * @param roomId The room ID
     * @param messageId The message ID
     * @param newEncryptedContent The new encrypted content
     */
    function editMessage(
        uint256 roomId,
        uint256 messageId,
        bytes calldata newEncryptedContent
    ) external {
        require(userProfiles[msg.sender].exists, "User must be registered");
        require(rooms[roomId].exists, "Room does not exist");
        require(messages[roomId][messageId].sender == msg.sender, "Only sender can edit");
        require(messages[roomId][messageId].timestamp > 0, "Message does not exist");

        messages[roomId][messageId].encryptedContent = newEncryptedContent;
        messages[roomId][messageId].edited = true;
        messages[roomId][messageId].editTimestamp = block.timestamp;

        emit MessageEdited(roomId, messageId, msg.sender, block.timestamp);
    }

    /**
     * @dev Get message count for a room
     * @param roomId The room ID
     * @return count The number of messages in the room
     */
    function getRoomMessageCount(uint256 roomId) external view returns (uint256) {
        return roomMessageCounts[roomId];
    }

    /**
     * @dev Get message sender and metadata (content is encrypted)
     * @param roomId The room ID
     * @param messageId The message ID
     * @return sender Message sender address
     * @return timestamp Message timestamp
     * @return edited Whether message was edited
     * @return editTimestamp Edit timestamp (0 if not edited)
     */
    function getMessageMetadata(
        uint256 roomId,
        uint256 messageId
    ) external view returns (
        address sender,
        uint256 timestamp,
        bool edited,
        uint256 editTimestamp
    ) {
        require(messages[roomId][messageId].timestamp > 0, "Message does not exist");
        Message memory msgData = messages[roomId][messageId];
        return (msgData.sender, msgData.timestamp, msgData.edited, msgData.editTimestamp);
    }

    /**
     * @dev Get encrypted message content (requires FHE decryption on client)
     * @param roomId The room ID
     * @param messageId The message ID
     * @return encryptedContent The encrypted message content
     */
    function getEncryptedMessage(
        uint256 roomId,
        uint256 messageId
    ) external view returns (bytes memory) {
        require(messages[roomId][messageId].timestamp > 0, "Message does not exist");
        return messages[roomId][messageId].encryptedContent;
    }
}

contracts/TicTacToeFHE.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// Импортируем библиотеку FHEVM от Zama
// В реальном проекте это будет импорт из установленного пакета
// import {FHE} from "fhevm/contracts/FHE.sol";

/**
 * @title TicTacToeFHE
 * @dev Контракт для игры в крестики-нолики с использованием FHEVM от Zama
 * 
 * КАК ЭТО РАБОТАЕТ:
 * - Ходы игроков хранятся в зашифрованном виде (euint8)
 * - Проверка победы происходит на зашифрованных данных
 * - Никто не может увидеть ходы противника до конца игры
 * - Использует релеер Zama для обработки FHE операций
 * 
 * ВАЖНО: Этот контракт требует настройки FHEVM библиотеки и релеера
 * Для работы нужны:
 * - Host contract адрес (ACL contract)
 * - KMS contract адрес
 * - Input verifier contract адрес
 * - Релеер URL
 * 
 * ПРИМЕЧАНИЕ: Это упрощенная версия. В реальном проекте нужно:
 * - Правильно настроить ACL для каждого зашифрованного значения
 * - Использовать fromExternal для валидации входных данных
 * - Правильно обрабатывать attestation от релеера
 */
contract TicTacToeFHE {
    // ВАЖНО: В реальном проекте здесь будет импорт FHE библиотеки
    // import {FHE, euint8, ebool} from "fhevm/contracts/FHE.sol";

    // Структура игры с зашифрованными данными
    struct Game {
        address player1;           // Игрок X (начинает первым)
        address player2;           // Игрок O
        address currentPlayer;      // Текущий игрок
        bytes32[9] encryptedBoard; // Зашифрованное поле (bytes32 = handle для euint8)
        bool isActive;             // Игра активна?
        address winner;            // Победитель (раскрывается только в конце)
        uint256 createdAt;         // Время создания
        uint8 moveCount;           // Счетчик ходов (для проверки ничьей)
    }

    // Маппинг: gameId => Game
    mapping(uint256 => Game) public games;

    // Счетчик игр
    uint256 public gameCounter;

    // Маппинг для отслеживания активных игр
    mapping(address => uint256[]) public playerActiveGames;

    // События
    event GameCreated(uint256 indexed gameId, address indexed player1, address indexed player2);
    event MoveMade(uint256 indexed gameId, address indexed player, uint8 position);
    event GameEnded(uint256 indexed gameId, address indexed winner);
    event PlayerJoined(uint256 indexed gameId, address indexed player2);
    event WinnerRevealed(uint256 indexed gameId, address indexed winner);

    /**
     * @dev Создать новую игру
     * @return gameId ID созданной игры
     * 
     * Функция:
     * - Создает новую игру с вызывающим адресом как player1
     * - Инициализирует пустое зашифрованное поле
     * - player2 должен присоединиться позже
     */
    function createGame() external returns (uint256) {
        uint256 gameId = gameCounter;

        // Инициализируем пустое поле (все handles = 0)
        bytes32[9] memory emptyBoard;

        games[gameId] = Game({
            player1: msg.sender,
            player2: address(0),
            currentPlayer: msg.sender,
            encryptedBoard: emptyBoard,
            isActive: true,
            winner: address(0),
            createdAt: block.timestamp,
            moveCount: 0
        });

        gameCounter++;
        playerActiveGames[msg.sender].push(gameId);

        emit GameCreated(gameId, msg.sender, address(0));
        return gameId;
    }

    /**
     * @dev Присоединиться к существующей игре как player2
     * @param gameId ID игры
     */
    function joinGame(uint256 gameId) external {
        Game storage game = games[gameId];
        require(game.isActive, "Game is not active");
        require(game.player2 == address(0), "Game already has two players");
        require(game.player1 != msg.sender, "Cannot join your own game");

        game.player2 = msg.sender;
        playerActiveGames[msg.sender].push(gameId);

        emit PlayerJoined(gameId, msg.sender);
    }

    /**
     * @dev Сделать зашифрованный ход
     * @param gameId ID игры
     * @param position Позиция на поле (0-8)
     * @param encryptedMove Зашифрованный ход (euint8 handle в виде bytes32)
     * @param attestation Аттестация от релеера (для валидации)
     * 
     * Функция:
     * - Принимает зашифрованный ход от игрока
     * - Валидирует его через attestation
     * - Сохраняет в зашифрованном виде на поле
     * - Проверяет победу на зашифрованных данных (через релеер)
     * 
     * ВАЖНО: В реальной реализации нужно:
     * 1. Использовать FHE.fromExternal для валидации encryptedMove
     * 2. Проверять ACL права
     * 3. Использовать FHE операции для проверки победы
     */
    function makeMove(
        uint256 gameId,
        uint8 position,
        bytes32 encryptedMove,
        bytes calldata attestation
    ) external {
        require(position < 9, "Invalid position");

        Game storage game = games[gameId];
        require(game.isActive, "Game is not active");
        require(game.player2 != address(0), "Waiting for second player");
        require(game.currentPlayer == msg.sender, "Not your turn");
        require(game.encryptedBoard[position] == bytes32(0), "Position already taken");

        // В реальной реализации здесь будет:
        // euint8 move = FHE.fromExternal(encryptedMove, attestation);
        // FHE.allow(move, address(this)); // Даем контракту доступ

        // Сохраняем зашифрованный ход
        game.encryptedBoard[position] = encryptedMove;
        game.moveCount++;

        // В реальной реализации проверка победы будет происходить через релеер:
        // ebool hasWon = checkWinnerFHE(game.encryptedBoard, msg.sender == game.player1);
        // Релеер обработает это и вернет результат

        // Для упрощения: если сделано 9 ходов, игра заканчивается ничьей
        if (game.moveCount >= 9) {
            game.isActive = false;
            emit GameEnded(gameId, address(0));
        } else {
            // Переключаем очередь
            game.currentPlayer = (game.currentPlayer == game.player1) 
                ? game.player2 
                : game.player1;
        }

        emit MoveMade(gameId, msg.sender, position);
    }

    /**
     * @dev Раскрыть победителя (вызывается после проверки через релеер)
     * @param gameId ID игры
     * @param winner Адрес победителя
     * 
     * ВАЖНО: В реальной реализации эта функция будет вызываться релеером
     * после обработки FHE проверки победы. Релеер расшифрует результат
     * и вызовет эту функцию для финализации игры.
     */
    function revealWinner(uint256 gameId, address winner) external {
        Game storage game = games[gameId];
        require(game.isActive, "Game is not active");
        // В реальной реализации здесь будет проверка подписи от релеера

        game.winner = winner;
        game.isActive = false;

        emit WinnerRevealed(gameId, winner);
        emit GameEnded(gameId, winner);
    }

    /**
     * @dev Получить информацию об игре (без расшифровки ходов)
     * @param gameId ID игры
     * @return player1 Адрес первого игрока
     * @return player2 Адрес второго игрока
     * @return currentPlayer Текущий игрок
     * @return isActive Активна ли игра
     * @return winner Победитель
     * @return moveCount Количество сделанных ходов
     */
    function getGame(uint256 gameId) external view returns (
        address player1,
        address player2,
        address currentPlayer,
        bool isActive,
        address winner,
        uint8 moveCount
    ) {
        Game memory game = games[gameId];
        return (
            game.player1,
            game.player2,
            game.currentPlayer,
            game.isActive,
            game.winner,
            game.moveCount
        );
    }

    /**
     * @dev Получить зашифрованное поле (только handles, не расшифрованные значения)
     * @param gameId ID игры
     * @return encryptedBoard Массив зашифрованных ходов
     */
    function getEncryptedBoard(uint256 gameId) external view returns (bytes32[9] memory) {
        return games[gameId].encryptedBoard;
    }

    /**
     * @dev Получить активные игры игрока
     * @param player Адрес игрока
     * @return activeGames Массив ID активных игр
     */
    function getPlayerActiveGames(address player) external view returns (uint256[] memory) {
        return playerActiveGames[player];
    }

    /**
     * @dev Проверить, является ли адрес победителем
     * @param gameId ID игры
     * @param player Адрес игрока
     * @return isWinner true если игрок победил
     */
    function isWinner(uint256 gameId, address player) external view returns (bool) {
        Game memory game = games[gameId];
        return game.winner == player;
    }
}

contracts/TicTacToeMock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title TicTacToeMock
 * @dev Мок контракт для игры в крестики-нолики БЕЗ использования FHEVM
 * Это упрощенная версия для тестирования логики игры
 * 
 * Как работает:
 * - Два игрока играют на поле 3x3
 * - Игрок 1 (X) начинает первым
 * - Игрок 2 (O) ходит вторым
 * - Победа определяется по стандартным правилам (3 в ряд)
 * - Все ходы хранятся в открытом виде (не зашифрованы)
 */
contract TicTacToeMock {
    // Структура игры
    struct Game {
        address player1;      // Игрок X (начинает первым)
        address player2;      // Игрок O
        address currentPlayer; // Текущий игрок, который должен ходить
        uint8[9] board;       // Поле 3x3 (0 = пусто, 1 = X, 2 = O)
        bool isActive;        // Игра активна?
        address winner;       // Победитель (address(0) если ничья или игра не закончена)
        uint256 createdAt;   // Время создания игры
    }

    // Маппинг: gameId => Game
    mapping(uint256 => Game) public games;

    // Счетчик игр
    uint256 public gameCounter;

    // Маппинг для отслеживания активных игр игрока
    mapping(address => uint256[]) public playerActiveGames;

    // События
    event GameCreated(uint256 indexed gameId, address indexed player1, address indexed player2);
    event MoveMade(uint256 indexed gameId, address indexed player, uint8 position, uint8 symbol);
    event GameEnded(uint256 indexed gameId, address indexed winner);
    event PlayerJoined(uint256 indexed gameId, address indexed player2);

    /**
     * @dev Создать новую игру
     * @return gameId ID созданной игры
     * 
     * Функция:
     * - Создает новую игру с вызывающим адресом как player1
     * - Инициализирует пустое поле
     * - player2 должен присоединиться позже через joinGame
     */
    function createGame() external returns (uint256) {
        uint256 gameId = gameCounter;

        games[gameId] = Game({
            player1: msg.sender,
            player2: address(0),
            currentPlayer: msg.sender,
            board: [uint8(0), 0, 0, 0, 0, 0, 0, 0, 0],
            isActive: true,
            winner: address(0),
            createdAt: block.timestamp
        });

        gameCounter++;
        playerActiveGames[msg.sender].push(gameId);

        emit GameCreated(gameId, msg.sender, address(0));
        return gameId;
    }

    /**
     * @dev Присоединиться к существующей игре как player2
     * @param gameId ID игры, к которой хотим присоединиться
     * 
     * Функция:
     * - Позволяет второму игроку присоединиться к игре
     * - Проверяет, что игра существует и player2 еще не присоединился
     * - Нельзя присоединиться к своей же игре
     */
    function joinGame(uint256 gameId) external {
        Game storage game = games[gameId];
        require(game.isActive, "Game is not active");
        require(game.player2 == address(0), "Game already has two players");
        require(game.player1 != msg.sender, "Cannot join your own game");

        game.player2 = msg.sender;
        playerActiveGames[msg.sender].push(gameId);

        emit PlayerJoined(gameId, msg.sender);
    }

    /**
     * @dev Сделать ход
     * @param gameId ID игры
     * @param position Позиция на поле (0-8, где 0 = верхний левый, 8 = нижний правый)
     * 
     * Функция:
     * - Проверяет, что игра активна и это ваш ход
     * - Проверяет, что позиция свободна
     * - Ставит ваш символ (X или O) на поле
     * - Проверяет победу после хода
     * - Переключает очередь на другого игрока
     * 
     * Позиции на поле:
     * 0 | 1 | 2
     * ---------
     * 3 | 4 | 5
     * ---------
     * 6 | 7 | 8
     */
    function makeMove(uint256 gameId, uint8 position) external {
        require(position < 9, "Invalid position");

        Game storage game = games[gameId];
        require(game.isActive, "Game is not active");
        require(game.player2 != address(0), "Waiting for second player");
        require(game.currentPlayer == msg.sender, "Not your turn");
        require(game.board[position] == 0, "Position already taken");

        // Определяем символ игрока (1 = X, 2 = O)
        uint8 symbol = (msg.sender == game.player1) ? 1 : 2;
        game.board[position] = symbol;

        // Проверяем победу
        address winnerCheck = checkWinner(game.board);
        if (winnerCheck != address(0)) {
            // Есть победитель - определяем кто именно
            game.winner = msg.sender; // Текущий игрок победил
            game.isActive = false;
            emit GameEnded(gameId, msg.sender);
        } else if (isBoardFull(game.board)) {
            // Ничья
            game.isActive = false;
            emit GameEnded(gameId, address(0));
        } else {
            // Переключаем очередь
            game.currentPlayer = (game.currentPlayer == game.player1) 
                ? game.player2 
                : game.player1;
        }

        emit MoveMade(gameId, msg.sender, position, symbol);
    }

    /**
     * @dev Проверить победителя
     * @param board Текущее состояние поля
     * @return winner Адрес победителя (address(0) если нет победителя)
     * 
     * Проверяет все возможные комбинации для победы:
     * - Горизонтальные линии (3 ряда)
     * - Вертикальные линии (3 столбца)
     * - Диагонали (2 диагонали)
     */
    function checkWinner(uint8[9] memory board) internal pure returns (address) {
        // Проверяем все возможные комбинации для победы
        // Массив из 8 линий, каждая содержит 3 позиции
        uint8[3][8] memory lines = [
            [uint8(0), 1, 2], // Верхняя горизонталь
            [uint8(3), 4, 5], // Средняя горизонталь
            [uint8(6), 7, 8], // Нижняя горизонталь
            [uint8(0), 3, 6], // Левая вертикаль
            [uint8(1), 4, 7], // Средняя вертикаль
            [uint8(2), 5, 8], // Правая вертикаль
            [uint8(0), 4, 8], // Главная диагональ
            [uint8(2), 4, 6]  // Побочная диагональ
        ];

        for (uint i = 0; i < 8; i++) {
            uint8 a = lines[i][0];
            uint8 b = lines[i][1];
            uint8 c = lines[i][2];

            if (board[a] != 0 && board[a] == board[b] && board[b] == board[c]) {
                // Есть победитель - возвращаем специальное значение
                // В makeMove мы определим, кто именно победил
                return address(1); // Временное значение, указывающее на победу
            }
        }

        return address(0);
    }

    /**
     * @dev Проверить, заполнено ли поле (ничья)
     * @param board Текущее состояние поля
     * @return full true если все клетки заняты
     */
    function isBoardFull(uint8[9] memory board) internal pure returns (bool) {
        for (uint i = 0; i < 9; i++) {
            if (board[i] == 0) {
                return false;
            }
        }
        return true;
    }

    /**
     * @dev Получить информацию об игре
     * @param gameId ID игры
     * @return player1 Адрес первого игрока
     * @return player2 Адрес второго игрока
     * @return currentPlayer Текущий игрок
     * @return board Состояние поля
     * @return isActive Активна ли игра
     * @return winner Победитель
     */
    function getGame(uint256 gameId) external view returns (
        address player1,
        address player2,
        address currentPlayer,
        uint8[9] memory board,
        bool isActive,
        address winner
    ) {
        Game memory game = games[gameId];
        return (
            game.player1,
            game.player2,
            game.currentPlayer,
            game.board,
            game.isActive,
            game.winner
        );
    }

    /**
     * @dev Получить активные игры игрока
     * @param player Адрес игрока
     * @return activeGames Массив ID активных игр
     */
    function getPlayerActiveGames(address player) external view returns (uint256[] memory) {
        return playerActiveGames[player];
    }

    /**
     * @dev Проверить, является ли адрес победителем в игре
     * @param gameId ID игры
     * @param player Адрес игрока
     * @return isWinner true если игрок победил
     */
    function isWinner(uint256 gameId, address player) external view returns (bool) {
        Game memory game = games[gameId];
        return game.winner == player;
    }
}

